import { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import SmartContrat from './artifacts/contracts/SmartContrat.sol/SmartContrat.json';
import './App.css';
import dataNft from './_metadata.json';

const smartcontratAdress = '0x8fD4b4df71A1fd3389c855b0d5baC901F5c147dC';

function NFTCard({ nft, mintFunction }) {
  return (
      <div className="nft-card">
        <h2>{nft.name}</h2>
        <img src={nft.image.replace('ipfs://', 'https://ipfs.io/ipfs/')} alt={nft.name} />
        <button onClick={() => mintFunction(nft.edition)}>Mint NFT {nft.edition}</button>
      </div>
  );
}

function App() {
  const [error, setError] = useState('');
  const [data, setData] = useState({});
  const [account, setAccount] = useState([]);
  const [nftData, setNftData] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [slideWidth, setSlideWidth] = useState(0);


  useEffect(() => {
    fetchData();
    getAccounts();
    setNftData(dataNft);

  }, []);



  async function getAccounts() {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({method: 'eth_requestAccounts'});
        setAccount(accounts);
        console.log(accounts[0]);
      } catch (err) {
        setError(err.message);
      }
    }
  }

  async function fetchData() {
    if (typeof window.ethereum !== 'undefined') {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const contract = new ethers.Contract(smartcontratAdress, SmartContrat.abi, provider);
      try {
        const cost = await contract.cost();
        const totalSupply = await contract.totalSupply();
        const object = {cost: String(cost), totalSupply: String(totalSupply)};
        setData(object);
      } catch (err) {
        setError(err.message);
      }
    }
  }

  async function mint(edition) {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({method: 'eth_requestAccounts'});
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        const contract = new ethers.Contract(smartcontratAdress, SmartContrat.abi, signer);
        const overrides = {
          from: accounts[0],
          value: ethers.utils.parseEther(String(Number(data.cost) / 10 ** 18)),
          gasLimit: 2900000,
        };
        const transaction = await contract.mint(accounts[0], edition, overrides);
        await transaction.wait();
        fetchData();
      } catch (err) {
        setError(err.message);
      }
    }
  }

  async function withdraw() {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({method: 'eth_requestAccounts'});
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        const contract = new ethers.Contract(smartcontratAdress, SmartContrat.abi, signer);
        const transaction = await contract.withdraw();
        await transaction.wait();
        fetchData();
      } catch (err) {
        setError(err.message);
      }
    }
  }
  // handle slide functions
  const handlePrevSlide = () => {
    setCurrentIndex(currentIndex === 0 ? nftData.length - 1 : currentIndex - 1);
  };

  const handleNextSlide = () => {
    setCurrentIndex(currentIndex === nftData.length - 1 ? 0 : currentIndex + 1);
  };

  return (
      <div className="App">
        {account[0] === "0x0ca22262c953bf13f89be2e1ff1742f9d227b18c" && (
            <button className="withdraw" onClick={withdraw}>
              Withdraw
            </button>
        )}
        <div className="container">
          <div className="nft-carousel" style={{ overflow: 'hidden' }}>
            <div className="nft-carousel-inner" style={{ transform: `translateX(-${currentIndex * slideWidth}px)`, display: 'flex', width: `${nftData.length * slideWidth}px` }}>
              {nftData.map((nft, index) => (
                  <NFTCard key={nft.edition} nft={nft} mintFunction={mint}/>
              ))}
            </div>
            <button className="nft-carousel-button nft-carousel-button--prev" onClick={handlePrevSlide}>
              Previous
            </button>
            <button className="nft-carousel-button nft-carousel-button--next" onClick={handleNextSlide}>
              Next
            </button>
          </div>
          {error && <p>{error}</p>}
          <h1>Mint a Jox NFT</h1>
          <p className="count">
            {data.totalSupply}/{data.maxSupply}
          </p>
          <p className="cost">
            Jox Collection NFT cost {Number(data.cost) / 10 ** 18} ETH (excluding gas fees)
          </p>
        </div>
      </div>
  );
}
  export default App;
