const App = () => {
  const [error, setError] = useState('');
  const [data, setData] = useState({});
  const [account, setAccount] = useState([]);
  const [nftData, setNftData] = useState([]);

  useEffect(() => {
    fetchData();
    getAccounts();
    setNftData(dataNft);
  }, []);

  const getAccounts = async () => {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({method: 'eth_requestAccounts'});
        setAccount(accounts);
        console.log(accounts[0]);
      } catch (err) {
        setError(err.message);
      }
    }
  };

  const fetchData = async () => {
    if (typeof window.ethereum !== 'undefined') {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const contract = new ethers.Contract(smartcontratAdress, SmartContrat.abi, provider);
      try {
        const cost = await contract.cost();
        const totalSupply = await contract.totalSupply();
        const object = {cost: String(cost), totalSupply: String(totalSupply)};
        setData(object);
      } catch (err) {
        setError(err.message);
      }
    }
  };

  async function mint(edition) {
    if (typeof window.ethereum !== "undefined") {
      try {
        const accounts = await window.ethereum.request({
          method: "eth_requestAccounts",
        });
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        const contract = new ethers.Contract(
            smartcontratAdress,
            SmartContrat.abi,
            signer
        );

        const overrides = {
          from: accounts[0],
          value: ethers.utils.parseEther(
              String(Number(data.cost) / 10 ** 18)
          ),
          gasLimit: 9900000,
        };
        const transaction = await contract.mint(accounts[0], edition, overrides);
        await transaction.wait();
        fetchData();
      } catch (err) {
        setError(err.message);
      }
    }
  }

  async function setNftPrice(edition, price, fetchData, setError) {
    if (typeof window.ethereum !== "undefined") {
      try {
        const accounts = await window.ethereum.request({
          method: "eth_requestAccounts",
        });
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        const contract = new ethers.Contract(
            smartcontratAdress,
            SmartContrat.abi,
            signer
        );

        const contractOwner = await contract.owner();
        if (accounts[0].toLowerCase() !== contractOwner.toLowerCase()) {
          throw new Error("Only the contract owner can set the NFT price.");
        }

        const overrides = {
          from: accounts[0],
          gasLimit: 2900000,
        };
        if (!Number.isInteger(edition) || edition <= 0) {
          throw new Error("NFT ID must be a positive integer greater than 0.");
        }

        const transaction = await contract.setNftPrice(
            edition,
            ethers.utils.parseEther(price),
            overrides
        );
        await transaction.wait();
        fetchData();
      } catch (err) {
        setError(err.message);
      }
    }
  }


  async function withdraw() {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({method: 'eth_requestAccounts'});
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        const contract = new ethers.Contract(smartcontratAdress, SmartContrat.abi, signer);
        const transaction = await contract.withdraw();
        await transaction.wait();
        fetchData();
      } catch (err) {
        setError(err.message);
      }
    }
  }

  return (

      <div className="App">
        {account[0] === "0x0ca22262c953bf13f89be2e1ff1742f9d227b18c" && (
            <button className="withdraw" onClick={withdraw}>
              Withdraw
            </button>
        )}
        <div className="container">
          <div className="nft-gallery">
            {nftData.map((nft) => (
                <NFTCard key={nft.edition}
                         nft={nft}
                         mintFunction={mint}
                         setPriceFunction={setNftPrice}
                         fetchData={fetchData}
                         setError={setError}/>
            ))}
          </div>
          {error && <p>{error}</p>}
          <h1>Mint a Jox NFT</h1>
          <p className="count">
            {data.totalSupply}/{data.maxSupply}
          </p>
          <p className="cost">
            Jox Collection NFT cost {Number(data.cost) / 10 ** 18} ETH (excluding gas fees)
          </p>
        </div>
      </div>
  );
}
export default App;
