import { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import SmartContrat from './artifacts/contracts/SmartContrat.sol/SmartContrat.json';
import './App.css';
import dataNft from './_metadata.json';

const smartcontratAdress = '0x264f6dCaBCB0d510613b11Df4ab22ab76b25DFa3';

function SetCostForm({ setCostFunction }) {
  const [cost, setCost] = useState(0);

  const handleSubmit = (e) => {
    e.preventDefault();
    setCostFunction(cost);
  };

  return (
      <form onSubmit={handleSubmit}>
        <label>
          Set cost (in ETH):
          <input
              type="number"
              step="0.01"
              min="0"
              value={cost}
              onChange={(e) => setCost(e.target.value)}
          />
        </label>
        <button type="submit">Set Cost</button>
      </form>
  );
}

