function SetPriceForm({ nftId, setPriceFunction }) {
  const [price, setPrice] = useState(0);

  const handleSubmit = (e) => {
    e.preventDefault();
    setPriceFunction(nftId, price.toString());
  };

  return (
      <form onSubmit={handleSubmit}>
        <label>
          Set price (in ETH):
          <input
              type="number"
              step="0.01"
              min="0"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
          />
        </label>
        <button type="submit">Set Price</button>
      </form>
  );
}

function NFTCard({ nft, mintFunction, setPriceFunction }) {
  return (
      <div className="nft-card">
        <h2>{nft.name}</h2>
        <img
            className="nftImage"
            src={nft.image.replace("ipfs://", "https://ipfs.io/ipfs/")}
            alt={nft.name}
        />
        <button onClick={() => mintFunction(nft.edition)}>
          Mint NFT {nft.edition}
        </button>
        <SetPriceForm nftId={nft.edition} setPriceFunction={setPriceFunction} />
        <button onClick={() => setPriceFunction(nft.edition, "0.05")}>
          Set price to 0.05 ETH
        </button>
      </div>
  );
}


