import { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import SmartContrat from './artifacts/contracts/SmartContrat.sol/SmartContrat.json';
import './App.css';
import dataNft from './_metadata.json';

const smartcontratAdress = '0x25E1e87d8755538c4185AF53d1741059F91B81c8';

function NFTCard({ nft, mintFunction }) {
  return (
      <div className="nft-card">
        <h2>{nft.name}</h2>
        <img  className='nftImage' src={nft.image.replace('ipfs://', 'https://ipfs.io/ipfs/')} alt={nft.name} />
        <button onClick={() => mintFunction(nft.edition)}>Mint NFT {nft.edition}</button>
      </div>
  );
}

function App() {
  const [error, setError] = useState('');
  const [data, setData] = useState({});
  const [account, setAccount] = useState([]);
  const [nftData, setNftData] = useState([]);

  useEffect(() => {
    fetchData();
    getAccounts();
    setNftData(dataNft);
  }, []);
  useEffect(() => {
    const startIndex = currentPage * nftPerPage;
    const endIndex = startIndex + nftPerPage;
    setNftPage(nftData.slice(startIndex, endIndex));
  }, [currentPage, nftData]);
  async function getAccounts() {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({method: 'eth_requestAccounts'});
        setAccount(accounts);
        console.log(accounts[0]);
      } catch (err) {
        setError(err.message);
      }
    }
  }

  async function fetchData() {
    if (typeof window.ethereum !== 'undefined') {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const contract = new ethers.Contract(smartcontratAdress, SmartContrat.abi, provider);
      try {
        const cost = await contract.cost();
        const totalSupply = await contract.totalSupply();
        const object = {cost: String(cost), totalSupply: String(totalSupply)};
        setData(object);
      } catch (err) {
        setError(err.message);
      }
    }
  }

  async function mint(edition) {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({method: 'eth_requestAccounts'});
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        const contract = new ethers.Contract(smartcontratAdress, SmartContrat.abi, signer);
        const overrides = {
          from: accounts[0],
          value: ethers.utils.parseEther(String(Number(data.cost) / 10 ** 18)),
          gasLimit: 2900000,
        };
        const transaction = await contract.mint(accounts[0], edition, overrides);
        await transaction.wait();
        fetchData();
      } catch (err) {
        setError(err.message);
      }
    }
  }

  async function withdraw() {
    if (typeof window.ethereum !== 'undefined') {
      try {
        const accounts = await window.ethereum.request({method: 'eth_requestAccounts'});
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        const contract = new ethers.Contract(smartcontratAdress, SmartContrat.abi, signer);
        const transaction = await contract.withdraw();
        await transaction.wait();
        fetchData();
      } catch (err) {
        setError(err.message);
      }
    }
  }
  const nftPerPage = 6;
  const totalPages = Math.ceil(nftData.length / nftPerPage);
  const [currentPage, setCurrentPage] = useState(0);

  function goToNextPage() {
    setCurrentPage(currentPage === totalPages - 1 ? 0 : currentPage + 1);
  }

  function goToPreviousPage() {
    setCurrentPage(currentPage === 0 ? totalPages - 1 : currentPage - 1);
  }

  const nftPage = nftData.slice(currentPage * nftPerPage, (currentPage + 1) * nftPerPage);


  return (
      <div className="App">
        {account[0] === "0x0ca22262c953bf13f89be2e1ff1742f9d227b18c" && (
            <button className="withdraw" onClick={withdraw}>
              Withdraw
            </button>
        )}
        <div className="carousel-container">
          <div className="carousel-images" style={{ transform: `translateX(-${currentPage * 100}%)` }}>
            {nftPage.map((nft) => (
                <NFTCard key={nft.edition} nft={nft} mintFunction={mint} />
            ))}
          </div>
        </div>
        <div className="pagination">
          <button onClick={goToPreviousPage}>Previous</button>
          <span>Page {currentPage + 1} of {totalPages}</span>
          <button onClick={goToNextPage}>Next</button>
        </div>
        {/*<div className="container">*/}
        {/*  <div className="nft-gallery">*/}
        {/*    {nftData.map((nft) => (*/}
        {/*        <NFTCard key={nft.edition} nft={nft} mintFunction={mint}/>*/}
        {/*    ))}*/}
        {/*  </div>*/}
          {error && <p>{error}</p>}
          <h1>Mint a Jox NFT</h1>
          <p className="count">
            {data.totalSupply}/{data.maxSupply}
          </p>
          <p className="cost">
            Jox Collection NFT cost {Number(data.cost) / 10 ** 18} ETH (excluding gas fees)
          </p>
        </div>
  );
}
  export default App;
